delete from stkinfo where EXCHID='6'  and stkid in('810005','810006','832586');
delete from internalorder where exchid='6' and contractnum in('00001216','00001217');
update internalorder  a  set  a.knocktime=(select    substr(b.tradedate,0,8)||substr(a.knocktime,9,14)       from sysconfig b );
update internalorder  a  set  a.sendtime=(select    substr(b.tradedate,0,8)||substr(a.sendtime,9,14)       from sysconfig b );
update  internalorder  a  set  a.ordertime=(select    substr(b.tradedate,0,8)||substr(a.ordertime,9,14)       from sysconfig b );
update  internalorder  a  set  a.returntime=(select    substr(b.tradedate,0,8)||substr(a.returntime,9,14)       from sysconfig b );
delete  from stklist where exchid='6' and OFFERREGID in('GZ11721600','GZ11721400') and stkid  in ('832586', '810005');
